mapper.select:
SELECT TO_CHAR(IDX) AS BOARDIDX, TO_CHAR(MENU) AS BOARDMENU, TO_CHAR(TITLE) AS BOARDTITLE, TO_CHAR(WRITER) AS BOARDWRITER, TO_CHAR(INDATE) AS BOARDINDATE, TO_CHAR(NVL(BOARDUPDATE, '-')) AS BOARDUPDATE, TO_CHAR(NVL(READCOUNT, '0')) AS READCOUNT FROM (SELECT ROWNUM AS NUM, BOARD.* FROM BOARD
AND MENU LIKE #{menuname}
AND TITLE LIKE '%' || #{searchText} || '%'
AND CONTENT LIKE '%' || #{searchText} || '%'
AND WRITER LIKE '%' || #{searchText} || '%'
ORDER BY IDX DESC) WHERE NUM BETWEEN #{startList} AND #{endList}
